import React, {Component} from "react";
import {ScrollView, Text , View} from "react-native";
import {Card} from "react-native-elements";
import {connect} from "react-redux"
import {baseUrl} from "../shared/baseUrl";




const mapStateToProps = state => {
    return {
        dishes: state.dishes,
        comments: state.comments,
        leaders: state.leaders,
        promotions: state.promotions
    }
};


function RenderCard(props) {

    const item = props.item;

  if (item != null) {
   return (<Card
  featuredTitle={item.name}
  featuredSubtitle={item.designation}
  image={{uri: baseUrl + item.image}}
   >
<Text
style={{margin:10}}
>{item.description}</Text>

   </Card>)


  }
  else {return (<View>lalalal</View>)}

}



class Home extends Component{

    render() {
        return (
                <ScrollView>
                <RenderCard item={this.props.dishes.dishes.filter((item) => item.featured)[0]} />
                <RenderCard item={this.props.leaders.leaders.filter((item) => item.featured)[0]}/>
                <RenderCard item={this.props.promotions.promotions.filter((item) => item.featured)[0]}/>
                </ScrollView>


        );
    }

}


export default connect(mapStateToProps) (Home);
