import React, {Component} from "react";
import {ScrollView, Text , View} from "react-native";
import {Card} from "react-native-elements";
import {connect} from "react-redux"
import {baseUrl} from "../shared/baseUrl";
import Loading from "./LoadingComponent";




const mapStateToProps = state => {
    return {
        dishes: state.dishes,
        comments: state.comments,
        leaders: state.leaders,
        promotions: state.promotions
    }
};


function RenderCard(props) {

    const item = props.item;

    if (props.isLoading) {
        return(
            <Loading />
        );
    }
    else if (props.errMess) {
        return(
            <View>
                <Text>{props.errMess}</Text>
            </View>
        );
    }
    else {

        if (item != null) {
            return (<Card
                featuredTitle={item.name}
                featuredSubtitle={item.designation}
                image={{uri: baseUrl + item.image}}
            >
                <Text
                    style={{margin: 10}}
                >{item.description}</Text>

            </Card>)


        } else {
            return (<View></View>)
        }
    }
}



class Home extends Component{

    render() {
        return (
                <ScrollView>
                <RenderCard
                    isLoading={this.props.dishes.isLoading}
                    erreMess={this.props.dishes.errMess}
                    item={this.props.dishes.dishes.filter((item) => item.featured)[0]} />
                <RenderCard
                    isLoading={this.props.leaders.isLoading}
                    erreMess={this.props.leaders.errMess}
                    item={this.props.leaders.leaders.filter((item) => item.featured)[0]}/>
                <RenderCard
                    isLoading={this.props.promotions.isLoading}
                    erreMess={this.props.promotions.errMess}
                    item={this.props.promotions.promotions.filter((item) => item.featured)[0]}/>
                </ScrollView>


        );
    }

}


export default connect(mapStateToProps)(Home);
