import React, {Component} from "react";
import {ScrollView, Text , View} from "react-native";
import {Card} from "react-native-elements";
import {DISHES} from "../shared/dishes";
import {LEADERS} from "../shared/leaders";
import {PROMOTIONS} from "../shared/promotions";


function RenderCard(props) {

    const item = props.item;

  if (item != null) {
   return (<Card
  featuredTitle={item.name}
  featuredSubtitle={item.designation}
  image={require("./images/uthappizza.png")}
   >
<Text
style={{margin:10}}
>{item.description}</Text>

   </Card>)


  }
  else {return (<View>lalalal</View>)}

}



class Home extends Component{

    constructor(props) {
        super(props);
        this.state = {
            dishes:DISHES,
            leaders: LEADERS,
            promotions: PROMOTIONS
        }
    }

    render() {
        return (
                <ScrollView>
                    <Text>ddddd</Text>
                <RenderCard item={this.state.dishes.filter((item) => item.featured)[0]} />
                <RenderCard item={this.state.leaders.filter((item) => item.featured)[0]}/>
                <RenderCard item={this.state.promotions.filter((item) => item.featured)[0]}/>
                </ScrollView>


        );
    }

}


export default Home;
