import React , {Component} from "react"
import {View , Text , ScrollView, Picker , StyleSheet, Button , Switch} from "react-native";
import DatePicker from "react-native-datepicker";
import {Card} from "react-native-elements";


class Reserve extends Component{

    constructor(props) {
        super(props);
        this.state = {
            smoking: false,
            date:"",
            guests:1
        }
    }

    handleReservation() {
        console.log(JSON.stringify(this.state));
        this.setState({smoking:true , date:"" , guests:1});
    }

    render() {
        return (
            <ScrollView>
            <View>
                <Text style={styles.fromLabel}> Number of Guests ?</Text>
                <Picker
                style={styles.formItem}
                selectedValue={this.state.guests}
                onValueChange={((itemValue, itemIndex) => this.setState({guests: itemValue}))}
                >
                    <Picker.Item value="1" label="1"/>
                    <Picker.Item value="2" label="2"/>
                    <Picker.Item value="3" label="3"/>
                    <Picker.Item value="4" label="4"/>
                    <Picker.Item value="5" label="5"/>
                    <Picker.Item value="6" label="6"/>
                </Picker>
            </View>
                <View style={styles.formContainer}>
                    <Text style={styles.fromLabel}>Smoking/Non-smoking ?</Text>
                    <Switch
                    style={styles.formItem}
                    value={this.state.smoking}
                    trackColor="#512DA8"
                    onValueChange={(value => this.setState({smoking: value}))}
                    ></Switch>
                </View>
                <View style={styles.formContainer}>
                    <Text>Please pick a date.</Text>
                    <DatePicker
                    style={{flex:2 , marginRight:20}}
                    date={this.state.date}
                    format=""
                    placeholder="Please select a date"
                    minDate="2020-07-17"
                    confirmBtnText="Confirm"
                    cancelBtnText="Cancel"
                    customStyles={ {
                        dateIcon: {
                            position:"absolute",
                            left:0,
                            top:4,
                            marginLeft:0
                        },
                        dateInput:{
                            marginLeft: 36
                        }
                    }}
                    onDateChange={(date) => {this.setState({date:date})}}
                    ></DatePicker>
                </View>
                <View style={styles.formContainer}>
                    <Button color="#512DA8" title="Reserve" onPress={() => this.handleReservation} accessibilityLabel="Learn more about this purple button"/>
                </View>

            </ScrollView>

        );
    }

}
const styles = StyleSheet.create (
    {

        formContainer : {
            flexDirection:"row",
            flex:1,
            alignItems:"center",
            justifyContent:"center",
            margin:20
        },

    fromLabel: {
        flex:2,
        fontSize:18
    },

    formItem: {
            flex:1,
    }
    }
);


export default Reserve;
