import React, {Component} from "react";
import {ScrollView,View , FlatList} from "react-native";
import {LEADERS} from "../shared/leaders";
import {Card, ListItem} from "react-native-elements";
import Text from "react-native-web/dist/exports/Text";

class AboutUs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            leaders:LEADERS
        }
    }

    render() {

       const RenderLeaders = ({item , index}) => {
         return (<ListItem
         title = {item.name}
         key = {index}
         subtitle={item.description}
         hideChevron = {true}
         leftAvatar={{source: require("./images/alberto.png")}}
         ></ListItem>
         )
       };

       function RenderHistory(props) {
        return (
            <Card title="Our History"><Text>Started in 2010, Ristorante con Fusion quickly established itself as a culinary icon par excellence in Hong Kong. With its unique brand of world fusion cuisine that can be found nowhere else, it enjoys patronage from the A-list clientele in Hong Kong.  Featuring four of the best three-star Michelin chefs in the world, you never know what will arrive on your plate the next time you visit us.

                The restaurant traces its humble beginnings to The Frying Pan, a successful chain started by our CEO, Mr. Peter Pan, that featured for the first time the world's best cuisines in a pan.</Text> </Card>
        )
       }


        return (

            <ScrollView>
          <RenderHistory/>
            <FlatList data={this.state.leaders} renderItem={RenderLeaders} keyExtractor={item => item.id.toString()}></FlatList>
            </ScrollView>

        );
    }

}

export default AboutUs;
