import React , {Component} from "react"
import {View , Text , Picker , StyleSheet, Button , Switch , Alert} from "react-native";
import DatePicker from "react-native-datepicker";
import * as Animatable from "react-native-animatable"
import {Notifications} from "expo"
import * as Permissions from "expo-permissions"

class Reserve extends Component{

    constructor(props) {
        super(props);
        this.state = {
            smoking: false,
            date:"",
            guests:1,
            showModal:false
        }
    }

    async getPermissions() {
        let permission = await Permissions.getAsync(Permissions.USER_FACING_NOTIFICATIONS);
        if(permission.status !== "granted")
        {
            permission = await Permissions.getAsync(Permissions.USER_FACING_NOTIFICATIONS);
            if(permission !== "granted")
            {Alert.alert("Permission not granted to show notifications")}
        }
        else {platform}
        return permission
    }

    async presentLocalNotification(date) {
        await this.getPermissions();
      Notifications.presentLocalNotificationAsync(
            {
                title:"Your Reservation",
                body:"Reservation for" + date + " requested",
                ios:{
                    sound:true
                },
                android:{
                    channelId:"Confusion",
                    color:"#512DA8"
                }

            }
        );


    }

    resetForm() {
        this.setState({smoking:false , date:"" , guests:1});
    }

    handleReservation() {
        console.log(JSON.stringify(this.state));
    }

    toggleModal() {
        this.setState({showModal: !this.state.showModal});
    }

    smoking() {
       if (this.state.smoking)
           return "Yes";
        else
            return "No"
    }

    formAlert = () =>
        Alert.alert(
            "Your Reservation OK?",
            `Number of guests: ${this.state.guests}\n Smoking? ${this.smoking()}\n Date and Time ${this.state.date}\n` ,
            [
                {
                    text:"Ok",
                    onPress: () => console.log(JSON.stringify(this.state),this.resetForm()),
                },
                {
                    text:"Cancel",
                    onPress:() => this.resetForm(),
                    style:"cancel"
                }
            ],
            {cancelable:false}
        );

    render() {

        return (
            <Animatable.View animation="zoomIn" duration={2000}>
                <View style={styles.formContainer}>
                    <Text style={styles.fromLabel}>Number of Guests</Text>
                    <Picker
                        style={styles.formItem}
                        selectedValue={this.state.guests}
                        onValueChange={(itemValue, itemIndex) => this.setState({guests: itemValue})}>
                        <Picker.Item label="1" value="1" />
                        <Picker.Item label="2" value="2" />
                        <Picker.Item label="3" value="3" />
                        <Picker.Item label="4" value="4" />
                        <Picker.Item label="5" value="5" />
                        <Picker.Item label="6" value="6" />
                    </Picker>
                </View>
                <View style={styles.formContainer}>
                    <Text style={styles.fromLabel}>Smoking/Non-smoking ?</Text>
                    <Switch
                    style={styles.formItem}
                    value={this.state.smoking}
                    trackColor="#512DA8"
                    onValueChange={(value => this.setState({smoking: value}))}
                    ></Switch>
                </View>
                <View style={styles.formContainer}>
                    <Text>Please pick a date.</Text>
                    <DatePicker
                    style={{flex:2 , marginRight:20}}
                    date={this.state.date}
                    format=""
                    placeholder="Please select a date"
                    minDate="2020-07-17"
                    confirmBtnText="Confirm"
                    cancelBtnText="Cancel"
                    customStyles={ {
                        dateIcon: {
                            position:"absolute",
                            left:0,
                            top:4,
                            marginLeft:0
                        },
                        dateInput:{
                            marginLeft: 36
                        }
                    }}
                    onDateChange={(date) => {this.setState({date:date})}}
                    ></DatePicker>
                </View>
                <View style={styles.formContainer}>
                    <Button
                        onPress={() => {this.presentLocalNotification(this.state.date);this.formAlert()}}
                        title="Reserve"
                        color="#512DA8"
                        accessibilityLabel="Learn more about this purple button"
                    />
                </View>
            </Animatable.View>
        );
    }
}

const styles = StyleSheet.create (
    {

        formContainer : {
            flexDirection:"row",
            flex:1,
            alignItems:"center",
            justifyContent:"center",
            margin:20
        },

    fromLabel: {
        flex:2,
        fontSize:18
    },

    formItem: {
            flex:1,
    },
        modal:{justifyContent: 'center',
            margin: 20},
        modalTitle:{ fontSize: 24,
            fontWeight: 'bold',
            backgroundColor: '#512DA8',
            textAlign: 'center',
            color: 'white',
            marginBottom: 20},
        modalText:{ fontSize: 18,
            margin: 10}
    }
);

export default Reserve;
